function getUTCTime(){var e=new Date;return[e.getUTCFullYear(),e.getUTCMonth()+1,e.getUTCDate(),e.getUTCHours(),e.getUTCMinutes()]}function generateCode(e){for(var t=0,n=0;n<5;n++)t+=e[n];return 3351^t}function displayTimeAndCode(){var e=getUTCTime(),t=generateCode(e),e=e.join(":"),t=t.toString(),n=60-Date.now()/1e3%60;document.getElementById("time").innerHTML=e,document.getElementById("code").innerHTML=t,document.getElementById("progress").value=n}