function getUTCTime(){var date=new Date;return[date.getUTCFullYear(),date.getUTCMonth()+1,date.getUTCDate(),date.getUTCHours(),date.getUTCMinutes()]}function generateCode(utcTime){for(var sum=0,i=0;i<5;i++)sum+=utcTime[i];return 3351^sum}function displayTimeAndCode(){var utcTime=getUTCTime(),code=generateCode(utcTime),utcTime=utcTime.join(":"),code=code.toString(),remaining=60-Date.now()/1e3%60;document.getElementById("time").innerHTML=utcTime,document.getElementById("code").innerHTML=code,document.getElementById("progress").value=remaining}